import styled from "styled-components";

const TermsText = styled.div`
  padding-bottom: 100px;
`;

export const Title = styled.h2`
  margin-top: 0;
  margin-bottom: 20px;
  font-family: Poppins, sans-serif;
  color: #31355b;
  font-size: 45px;
  line-height: 1.4;
  font-weight: 700;
  text-align: left;
  letter-spacing: -1px;
  text-indent: -1px;
`;

export const SubTitle = styled.p`
  font-family: Bryantpro, sans-serif;
  font-size: 17;
  flex: 1;
  width: 640px;
  height: 24px;
`;

export const Text = styled.p`
  font-family: Bryantpro, sans-serif;
  font-size: 17;
  width: 640;
`;

export default function TermsComponent({ id }) {
  return (
    <TermsText id={id}>
      <Title>Terms of Service</Title>
      <SubTitle>OVERVIEW</SubTitle>
      <Text>
        This website is operated by Expetitle LLC. Throughout the site, the
        terms “we”, “us” and “our” refer to Expetitle LLC. Expetitle LLC offers
        this website, including all information, tools and services available
        from this site to you, the user, conditioned upon your acceptance of all
        terms, conditions, policies and notices stated here. By visiting our
        site and/ or purchasing something from us, you engage in our “Service”
        and agree to be bound by the following terms and conditions (“Terms of
        Service”, “Terms”), including those additional terms and conditions and
        policies referenced herein and/or available by hyperlink. These Terms of
        Service apply to all users of the site, including without limitation
        users who are browsers, vendors, customers, merchants, and/ or
        contributors of content.
        <br />
      </Text>
      <Text>
        Please read these Terms of Service carefully before accessing or using
        our website. By accessing or using any part of the site, you agree to be
        bound by these Terms of Service. If you do not agree to all the terms
        and conditions of this agreement, then you may not access the website or
        use any services. If these Terms of Service are considered an offer,
        acceptance is expressly limited to these Terms of Service.
        <br />
      </Text>
      <Text>
        SECTION 1 - TERMS
        <br />
      </Text>
      <Text>
        By agreeing to these Terms of Service, you represent that you are at
        least the age of majority in your state or province of residence, or
        that you are the age of majority in your state or province of residence
        and you have given us your consent to allow any of your minor dependents
        to use this site.
        <br />
      </Text>
      <Text>
        You may not use our Services for any illegal or unauthorized purpose nor
        may you, in the use of the Service, violate any laws in your
        jurisdiction (including but not limited to copyright laws).
        <br />
      </Text>
      <Text>
        You must not transmit any worms or viruses or any code of a destructive
        nature
        <br />
      </Text>
      <Text>
        A breach or violation of any of the Terms will result in an immediate
        termination of your Services.
        <br />
      </Text>
    </TermsText>
  );
}
