import { Link } from "react-scroll";
import "./App.css";
import styled from "styled-components";
import TermsComponent from "./Terms";
import PrivacyComponent from "./Privacy";

const BaseComponent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: row;
  margin-left: auto;
  margin-right: auto;
  max-width: 940px;
`;

const MenuComponent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  margin-top: 0;
  margin-bottom: -196px;
  padding-right: 0;
  padding-bottom: 0;
  padding-left: 0;
  width: 33%;
  position: relative;
  float: left;
  width: 100%;
  min-height: 1px;
  padding-left: 10px;
  padding-right: 10px;
`;

const LinkWord = styled(Link)`
  display: block;
  padding: 10px 20px;
  border-radius: 10px;
  color: #070f1a;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  &:hover{

    color: #ffb39a;
    cursor: pointer
  }
}
`;

const TextComponent = styled.div`
  dispaly: flex;
  flex: 1;
  flex-direction: column;
  padding-right: 0;
  font-family: Bryantpro, sans-serif;
  color: rgba(10, 6, 60, 0.83);
  font-size: 17px;
  font-weight: 400;
  margin-top: 0;
  margin-bottom: -196px;
  padding-right: 0;
  padding-bottom: 0;
  padding-left: 0;
  width: 67%;
`;

function App() {
  return (
    <nav>
      <BaseComponent>
        <MenuComponent>
          <div style={{ position: "sticky", top: 20, paddingRight: 20 }}>
            <LinkWord
              to="Terms"
              spy={true}
              activeClass="active"
              smooth={true}
              duration={200}
              style={{ color: "#ffb39a", backgroundColor: "#f2f4f8" }}
            >
              Terms of Service
            </LinkWord>
            <LinkWord to="Privacy" smooth={true} duration={200}>
              Privacy Policy
            </LinkWord>
          </div>
        </MenuComponent>
        <TextComponent>
          <TermsComponent id="Terms" />
          <PrivacyComponent id="Privacy" />
        </TextComponent>
      </BaseComponent>
    </nav>
  );
}

export default App;
