import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import "./App.css";
import styled from "styled-components";
import TermsComponent from "./Terms";
import PrivacyComponent from "./Privacy";

const Body = styled.div`
  height: auto;
  margin-right: auto;
  margin-left: auto;
  margin-top: 0;
  margin-bottom: 22px;
  padding-right: 0;
  padding-left: 0;
  font-family: Poppins, sans-serif;
  color: #070f1a;
  font-size: 20px;
  line-height: 1.4;
  font-weight: 400;
  min-height: 100%;
  max-width =${(props) => props.width}
  box-sizing: border-box;
`;

const BaseComponent = styled.div`
  display: flex;

  flex-direction: row;
  margin-left: auto;
  margin-right: auto;
  width: ${(props) => props.width}
  max-width: 940px;
  padding: 95px 0 275px;
`;

const MenuComponent = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  margin-top: 0;
  margin-bottom: -196px;
  padding-right: 0;
  padding-bottom: 0;
  padding-left: 0;
  width: 33%;
  position: relative;
  float: left;
  min-height: 1px;
`;

const TextComponent = styled.div`
  dispaly: flex;
  flex: 1;
  flex-direction: column;
  padding-right: 0;
  font-family: Bryantpro, sans-serif;
  color: rgba(10, 6, 60, 0.83);
  font-size: 17px;
  font-weight: 400;
  margin-top: 0;
  margin-bottom: -196px;
  padding-right: 0;
  padding-bottom: 0;
  padding-left: 0;
  width: 67%;
`;

const LinkWord = styled(Link)`
  display: block;
  padding: 10px 20px;
  border-radius: 10px;
  color: #070f1a;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  &:hover{

    color: #ffb39a;
    cursor: pointer
  }
}
`;

function App() {
  const [width, setWidth] = useState(0);

  useEffect(() => {
    updateDimensions();

    window.addEventListener("resize", updateDimensions);
    return () => {
      window.removeEventListener("resize", updateDimensions);
    };
  }, []);

  const updateDimensions = () => {
    console.log(window.innerWidth);
    setWidth(window.innerWidth);
  };

  return (
    // <Body width={width}>
    <BaseComponent width={width}>
      <MenuComponent>
        <div style={{ position: "sticky", top: 20, paddingRight: 20 }}>
          <LinkWord
            to="Terms"
            spy={true}
            activeClass="active"
            smooth={true}
            duration={200}
            style={{ color: "#ffb39a", backgroundColor: "#f2f4f8" }}
          >
            Terms of Service
          </LinkWord>
          <LinkWord to="Privacy" smooth={true} duration={200}>
            Privacy Policy
          </LinkWord>
        </div>
      </MenuComponent>
      <TextComponent>
        <TermsComponent id="Terms" />
        <PrivacyComponent id="Privacy" />
      </TextComponent>
    </BaseComponent>
    // </Body>
  );
}

export default App;
